mapper.select:
select homeDayRent_zch_hwz_gjc
from tab_home_zch_hwz_gjc
where homeId_zch_hwz_gjc = #{homeId_zch_hwz_gjc}

SELECT DISTINCT h.*
FROM tab_Home_zch_hwz_gjc h
         JOIN tab_order_zch_hwz_gjc o ON h.homeId_zch_hwz_gjc = o.homeId_zch_hwz_gjc
         JOIN tab_homeDevice_zch_hwz_gjc d ON h.homeId_zch_hwz_gjc = d.homeId_zch_hwz_gjc
         JOIN tab_homeInformation_zch_hwz_gjc i ON h.homeId_zch_hwz_gjc = i.homeId_zch_hwz_gjc
WHERE (#{endDate}  <  o.creationTime_zch_hwz_gjc OR #{beginDate}  >  o.endTime_zch_hwz_gjc OR
       o.creationTime_zch_hwz_gjc IS NULL)
AND h.homeAddress_zch_hwz_gjc LIKE CONCAT('%', #{address}, '%')
AND i.maxPerson_zch_hwz_gjc  >=  #{maxPeople}
AND h.homeType_zch_hwz_gjc  =  #{homeType}
AND h.homeDayRent_zch_hwz_gjc BETWEEN #{minRent} AND #{maxRent}
AND d.deviceName_zch_hwz_gjc IN

    #{deviceName}

GROUP BY h.homeId_zch_hwz_gjc
HAVING COUNT(DISTINCT d.deviceType_zch_hwz_gjc) = #{deviceCount}
LIMIT #{page}, #{size}
